import React, { useState } from "react";
import { Select, Button, Card } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { fetchRegionInfo } from "./RegionApi"; // Import the fetchRegionInfo function from the regionApi.js file
import { setRegion, setRegionInfo } from "../actions";
import "../App.css";

const { Option } = Select;

const Form = () => {
  const selectedRegion = useSelector((state) => state.region.selectedRegion);
  const regionInfo = useSelector((state) => state.region.regionInfo); // Assuming regionInfo is stored in the 'region' slice of the Redux store
  const coordinates = useSelector((state) => state.region.coordinates);
  const dispatch = useDispatch();
  const [loading, setLoading] = useState(false);

  const handleLoadClick = async () => {
    try {
      setLoading(true);
      switch (selectedRegion) {
        case "usa":
          await fetchRegionInfo("United States");
          break;
        case "india":
          await fetchRegionInfo("India");
          break;
        case "uk":
          await fetchRegionInfo("United Kingdom");
          break;
        default:
          dispatch(setRegionInfo("Please select a region."));
      }
    } catch (error) {
      dispatch(setRegionInfo(error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleRegionChange = (value) => {
    dispatch(setRegion(value));
  };

  return (
    <div className="floating-controls">
      <Select
        style={{ width: 200, marginRight: 16 }}
        placeholder="Select a region"
        onChange={handleRegionChange}
        value={selectedRegion || undefined}
      >
        <Option value="usa">United States</Option>
        <Option value="india">India</Option>
        <Option value="uk">United Kingdom</Option>
      </Select>
      <Button type="primary" onClick={handleLoadClick} loading={loading}>
        Load
      </Button>
      {regionInfo ? (
        <div className="card-container">
          <Card>
            <b>
              <p>{regionInfo}</p>
            </b>
          </Card>
        </div>
      ) : null}
    </div>
  );
};

export default Form;
