import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Card } from "antd";
import { fetchRegionInfo } from "./RegionApi";
import { setCoordinates } from "../actions";

const Cards = () => {
  const [regionInfo, setRegionInfo] = useState(null);
  const selectedRegion = useSelector((state) => state.region.selectedRegion);
  const dispatch = useDispatch();

  useEffect(() => {
    async function fetchRegionData() {
      try {
        const cardData = await fetchRegionInfo(selectedRegion);
        const { name, latlng, timezones, cca2 } = cardData;
        const regionInfoText = `Region: ${name.common}\nCoordinates: (${
          latlng[0]
        }, ${latlng[1]})${timezones ? `\nTimezone: ${timezones}` : ""}${
          cca2 ? `\nCountry Code: ${cca2}` : ""
        }`;
        setRegionInfo(regionInfoText);

        const coordinates = [latlng[0], latlng[1]];
        dispatch(setCoordinates(coordinates));
      } catch (error) {
        console.error("Error fetching region information:", error);
        setRegionInfo("Region information not found.");
      }
    }
    fetchRegionData();
  }, [selectedRegion, dispatch]);

  return (
    <div className="card-container">
      <Card>
        <b>
          <p>{regionInfo}</p>
        </b>
      </Card>
    </div>
  );
};

export default Cards;
