import { Select, Button, Card } from "antd";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import { setRegion, setRegionInfo } from "../actions";

const { Option } = Select;

const Floating = () => {
  const selectedRegion = useSelector((state) => state.region.selectedRegion);
  const regionInfo = useSelector((state) => state.region.regionInfo);
  const dispatch = useDispatch();

  const fetchRegionInfo = async (region) => {
    try {
      const response = await axios.get(
        //`https://nominatim.openstreetmap.org/search?q=${region}&format=json&limit=1`
        `https://restcountries.com/v3.1/name/India`
      );
      const data = response.data[0];
      console.log(data);
      if (data) {
        console.log(region);
        const regionInfo = `Region: ${data.display_name}\nCoordinates: (${
          data.lat
        }, ${data.lon})${data.timezone ? `\nTimezone: ${data.timezone}` : ""}${
          data.address?.country_code
            ? `\nCountry Code: ${data.address.country_code}`
            : ""
        }`;
        dispatch(setRegionInfo(regionInfo));
      } else {
        dispatch(setRegionInfo("Error: Region information not found."));
      }
    } catch (error) {
      console.error("Error fetching region information:", error);
      dispatch(
        setRegionInfo(
          "Error fetching region information. Please try again later."
        )
      );
    }
  };

  const handleLoadClick = () => {
    switch (selectedRegion) {
      case "usa":
        fetchRegionInfo("United States");
        break;
      case "india":
        fetchRegionInfo("India");
        break;
      case "uk":
        fetchRegionInfo("United Kingdom");
        break;
      default:
        dispatch(setRegionInfo("Please select a region."));
    }
  };

  const handleRegionChange = (value) => {
    dispatch(setRegion(value));
  };

  return (
    <div className="floating-controls">
      <Select
        style={{ width: 200, marginRight: 16 }}
        placeholder="Select a region"
        onChange={handleRegionChange}
        value={selectedRegion}
      >
        <Option value="usa">United States</Option>
        <Option value="india">India</Option>
        <Option value="uk">United Kingdom</Option>
      </Select>
      <Button type="primary" onClick={handleLoadClick}>
        Load
      </Button>
      <div className="card-container">
        <Card>{regionInfo}</Card>
      </div>
    </div>
  );
};

export default Floating;
