import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Card } from "antd";
import "../App.css";
import { setCoordinates } from "../actions";

const Cards = () => {
  const [regionInfo, setRegionInfo] = useState(""); // Set initial state to an empty string
  const selectedRegion = useSelector((state) => state.region.selectedRegion);
  const { name, latlng, timezones, cca2 } = useSelector(
    (state) => state.region.regionInfo
  );
  const dispatch = useDispatch();

  useEffect(() => {
    const regionInfoText = `Region: ${name}\n\n ${
      timezones ? `\nTimezone: ${timezones}` : ""
    }${cca2 ? `\nCountry Code: ${cca2}` : ""}`;
    const coordinates = [latlng?.[0] ?? 0, latlng?.[1] ?? 0];

    setRegionInfo(regionInfoText);
    dispatch(setCoordinates(coordinates));
  }, [selectedRegion, dispatch, name, latlng, timezones, cca2]);

  return regionInfo ? (
    <div className="card-container">
      <Card>
        <b>
          <p>{regionInfo}</p>
        </b>
      </Card>
    </div>
  ) : null;
};

export default Cards;
