const initialState = {
  selectedRegion: "",
  regionInfo: {
    name: "",
    latlng: [], // An array to store latitude and longitude
    timezone: "",
    cca2: "",
  },
};

const regionReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SET_REGION":
      return {
        ...state,
        selectedRegion: action.payload,
      };
    case "SET_REGION_INFO":
      return {
        ...state,
        regionInfo: {
          name: action.payload.name,
          lat: action.payload.latlng,
          timezone: action.payload.timezone,
          cca2: action.payload.cca2,
        },
      };
    default:
      return state;
  }
};

export default regionReducer;
