import React, { useLayoutEffect } from "react";
import { useSelector } from "react-redux";
import { useMapEvents } from "react-leaflet";
// import other necessary dependencies and components

function Move() {
  const regionInfo = useSelector((state) => state.region.coordinates);

  const position = [regionInfo[0], regionInfo[1]];

  const map = useMapEvents({
    click() {
      map.locate();
    },

    locationfound(e) {
      map.flyTo(position, map.getZoom());
    },
  });

  useLayoutEffect(() => {
    // Automatically call the click function after the component has rendered
    map.locate();
  }, []); // The empty dependency array ensures the effect runs only once when the component mounts

  // The rest of your component code and JSX
  // ...
}

export default Move;
