import React, { useEffect } from "react";
import { useMapEvents } from "react-leaflet";
import { useSelector } from "react-redux";

function Move() {
  const regionInfo = useSelector((state) => state.region.coordinates);
  const position = [regionInfo[0], regionInfo[1]];
  const map = useMapEvents({});

  useEffect(() => {
    // Automatically trigger the location update when the component mounts
    map.locate();
  }, [map]);

  useEffect(() => {
    // Update the map position whenever the regionInfo changes
    map.flyTo(position, map.getZoom());
  }, [map, regionInfo, position]);

  return null; // Return null as this component doesn't render anything
}

export default Move;
